mapper.insert:
insert into tbl_heart(hno, bno, user_id)
values(
	heart_seq.nextval,
	#{bno},
	#{user_id}
)

mapper.select:
select hno from tbl_heart where user_id = #{user_id} and bno = #{bno} 
union all 
select 0 as hno from dual where not exists (select hno from tbl_heart where user_id = #{user_id} and bno = #{bno})
